//Adapted from https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm - Finite Repetition

vec3 opRepLim(vec3 p,vec3 c,vec3 l){
    p+=(c*l)/2.0;
    return p-c*clamp(floor((p+0.0001)/c+0.5),vec3(0.0),l);
}

shader_model.instance:
//From https://www.shadertoy.com/view/wlyBWm by @paulofalcao

vec3 smoothrepeat_$name(vec3 p,vec3 smooth_size,vec3 size){
    size.$Axis/=2.0;
    float w=pow(2.0,float($Iterations));
	for(int i=0;i<$Iterations;i++){
        p.$Axis=sqrt(p.$Axis*p.$Axis+smooth_size.$Axis);//smooth abs
		p.$Axis-=size.$Axis*w;//translate
		w=w/2.0;
	}
	return p;
}

vec3 blakle_smoothrepeat_asin_sin_$name(vec3 p,vec3 smooth_size,vec3 size){
    vec3 t=p.xyz;
    t.$Axis/=size.$Axis;
    t.$Axis=(asin(sin(t.$Axis)*(1.0-smooth_size.$Axis)))*size.$Axis;
	p.$Axis=t.$Axis;
    return p;
}

shader_model.global:
vec3 MFSDF_smoothRot_X(vec3 p,float s,float m,float distance){
  s*=0.5;
  float x=asin(sin(atan(p.y,p.z)*s)/(m+1.0))/s;
  return vec3(p.x,sin(x)*length(p.yz),cos(x)*length(p.yz)-distance);
}

vec3 MFSDF_smoothRot_Y(vec3 p,float s,float m,float distance){
  s*=0.5;
  float x=asin(sin(atan(p.x,p.z)*s)/(m+1.0))/s;
  return vec3(sin(x)*length(p.xz),p.y,cos(x)*length(p.xz)-distance);
}

vec3 MFSDF_smoothRot_Z(vec3 p,float s,float m,float distance){
  s*=0.5;
  float x=asin(sin(atan(p.x,p.y)*s)/(m+1.0))/s;
  return vec3(sin(x)*length(p.xy),cos(x)*length(p.xy)-distance,p.z);
}

vec2 twist_rot(vec2 v, float a) {
	a=a*0.01745329251;
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(vec2(c, -s),vec2(s, c));
	return m * v;
}

vec3 twistX(vec3 v, float a){
	v.yz=twist_rot(v.yz,v.x*a);
	return v;
}

vec3 twistY(vec3 v, float a){
	v.xz=twist_rot(v.xz,v.y*a);
	return v;
}

vec3 twistZ(vec3 v, float a){
	v.xy=twist_rot(v.xy,v.z*a);
	return v;
}

