//tetrahedron normal by PauloFalcao
//https://www.shadertoy.com/view/XstGDS
vec3 normal_$name(vec3 p){  
  const vec3 e=vec3(0.01,-0.01,0.0);
  float v1=$mfsdf(vec4(p+e.xyy,0.0)).w;
  float v2=$mfsdf(vec4(p+e.yyx,0.0)).w;
  float v3=$mfsdf(vec4(p+e.yxy,0.0)).w;
  float v4=$mfsdf(vec4(p+e.xxx,0.0)).w;
  return normalize(vec3(v4+v1-v3-v2,v3+v4-v1-v2,v2+v4-v3-v1));
}

//By pauloFalcao
vec4 distortHeighByNormal_$name(vec4 uv){
    float d=$mfsdf(uv).w;
	if (d<=abs($Distort*($Bound+1.0))+0.01){
		vec3 n=normal_$name(uv.xyz);
		vec3 s=$tex3d(vec4(uv.xyz-d*n,0.0));
	    return $mfsdf(vec4(uv.xyz-n*s*$Distort,uv.w));
	} else {
		return vec4(vec3(0.0),d);
	}
}

vec4 for_fold_$name(vec4 p,float mfsdftype) {
  for(int i=0;i<$iterations;i++){
    p=$trans3d(p);
  }
  vec4 o=$obj3d(vec4(p.xyz,mfsdftype));
  return vec4(o.xyz,o.w/p.w);
}

vec4 for_min_$name(vec4 p,float mfsdftype) {
  vec4 d=$obj3d(vec4(p.xyz,mfsdftype));
  vec4 m=d;
  for(int i=0;i<$iterations;i++){
    p=$trans3d(p);
	d=$obj3d(vec4(p.xyz,mfsdftype));
	d.w=d.w/p.w;
	m=m.w<d.w?m:d;
  }
  return m;
}

vec4 for_custom_$name(vec4 p,float mfsdftype) {
  vec4 d=$obj3d(vec4(p.xyz,mfsdftype));
  vec4 m=d;
  for(int i=0;i<$iterations;i++){
    p=$trans3d(p);
	d=$obj3d(vec4(p.xyz,mfsdftype));
	float a=m.w;
	float b=d.w/p.w;
	float v=$custombool(vec2(a,b));
    float e=0.001;
	vec2 n=normalize(vec2($custombool(vec2(a+e,b))-$custombool(vec2(a-e,b)),
                          $custombool(vec2(a,b+e))-$custombool(vec2(a,b-e))));				
	m=vec4(mix(m.xyz,d.xyz,atan(abs(n.y),abs(n.x))/(3.14159265359/2.0)),v);
  }
  return vec4(m.xyz,m.w/(1.0+$Correction));
}

float blursdf3d_$name(in vec3 p,in float b,float f){
	float a=0.0;
	float d=$mfsdfa(vec4(p,0.0)).w;
	if (d<b*f){
		for(int i=0;i<$iterations;i++){
	        float y=1.0-2.0*(float(i)/float($iterations));
	        float r=sqrt(1.0-y*y);
	        float t=2.39996322973*float(i);
			a+=$mfsdfa(vec4(p+vec3(cos(t)*r,y,sin(t)*r)*b,0.0)).w;
		}
	} else{
		return d;
	}
	return a/$iterations.0;
}

