vec2 twist_rot(vec2 v, float a) {
	a=a*0.01745329251;
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(vec2(c, -s),vec2(s, c));
	return m * v;
}

vec3 twistX(vec3 v, float a){
	v.yz=twist_rot(v.yz,v.x*a);
	return v;
}

vec3 twistY(vec3 v, float a){
	v.xz=twist_rot(v.xz,v.y*a);
	return v;
}

vec3 twistZ(vec3 v, float a){
	v.xy=twist_rot(v.xy,v.z*a);
	return v;
}

