//from https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow_$name( in vec3 ro, in vec3 rd, in float mint, in float tmax){
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration
    for( int i=0; i<32; i++ ){
	    vec3 p=ro + rd*t;
		float h = mfsdf_$name(vec4(p,0.0)).w;
		float isLight=mfsdf_$name(vec4(p,13.0)).x;
