vec3 raymarch_$name(vec2 uv) {
    uv-=0.5;
	vec3 cam=vec3($CamX,$CamY,$CamZ)*$CamZoom;
	vec3 lookat=vec3($LookAtX,$LookAtY,$LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*$CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	float dS=0.0;
	march_$name(d,p,dS,ro,rd);

    vec3 color=vec3(0.0);
	vec3 objColor=mfsdf_$name(vec4(p,1.0)).xyz;   // 1 - BaseColor (r,g,b,sdf) linear (0-1)
	float objMetallic=clamp(mfsdf_$name(vec4(p,2.0)).x,0.0,1.0);  // 2 - Metallic  (v,0,0,sdf)
	float objSpecular=clamp(max(mfsdf_$name(vec4(p,4.0)).x*0.2,objMetallic),0.0,1.0);  // 4 - Specular  (v,0,0,sdf)
	float objRoughness=mfsdf_$name(vec4(p,5.0)).x; // 5 - Roughness (v,0,0,sdf)
	vec3 objEmission=mfsdf_$name(vec4(p,13.0)).xyz; // 13 - Emission (r,g,b,sdf)
	objColor=max(objColor,objEmission);
	vec3 light=normalize(vec3($SunX,$SunY,$SunZ));
	if (d<50.0) {
	    vec3 n=normal_$name(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_$name(p,n),$AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_$name(p,light,0.05,5.0),$Shadow);
		if (objEmission.x>0.0){
			color=objEmission;
		} else {
			color=min(vec3(max(shadow,$AmbLight)),max(l,$AmbLight))*max(cAO,$AmbLight)*((1.0-objMetallic)*objColor)+
			pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular*mix(vec3(1.0),objColor,objMetallic)*(1.0-objRoughness)*16.0;
		}
		//reflection
		d=0.01;
		march_$name(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		vec3 objEmissionRef=vec3(0);
		if (d<50.0) {
			objColorRef=mfsdf_$name(vec4(p,1.0)).xyz;
			objEmissionRef=mfsdf_$name(vec4(p,13.0)).xyz;
			n=normal_$name(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			if (objEmissionRef.x>0.0){
			  objColorRef=objEmissionRef;
			} else {
			  objColorRef=max(l,$AmbLight)*objColorRef;
			}
		} else {
			objColorRef=$hdri(Simple360HDR_equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,objSpecular*mix(vec3(1.0),objColor,objMetallic));
	} else {
		color=$hdri(Simple360HDR_equirectangularMap(rd.xzy)).xyz;
	}
	return pow(color,vec3(1.0/$Gamma));
}

