//Adapted from MaterialMaker

vec3 circle_repeat_transform_X(vec3 p, float count, float distance) {
	float r = 6.28/count;
	float pa = atan(p.y, p.z);
	float a = mod(pa+0.5*r, r)-0.5*r;
	vec3 rv;
	float c = cos(a-pa);
	float s = sin(a-pa);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c-distance;
	return rv;
}

vec3 circle_repeat_transform_Y(vec3 p, float count, float distance) {
	float r = 6.28/count;
	float pa = atan(p.x, p.z);
	float a = mod(pa+0.5*r, r)-0.5*r;
	vec3 rv;
	float c = cos(a-pa);
	float s = sin(a-pa);
	rv.x = p.x*c+p.z*s;
	rv.y = p.y;
	rv.z = -p.x*s+p.z*c-distance;
	return rv;
}

vec3 circle_repeat_transform_Z(vec3 p, float count, float distance) {
	float r = 6.28/count;
	float pa = atan(p.x, p.y);
	float a = mod(pa+0.5*r, r)-0.5*r;
	vec3 rv;
	float c = cos(a-pa);
	float s = sin(a-pa);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c-distance;
	rv.z = p.z;
	return rv;
}

