	vec3 light=normalize(vec3($SunX,$SunY,$SunZ));
	if (d<50.0) {
	    vec3 n=normal_$name(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_$name(p,n),$AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_$name(p,light,0.05,5.0),$Shadow);
		if (objEmission.x>0.0){
			color=objEmission;
		} else {
			color=min(vec3(max(shadow,$AmbLight)),max(l,$AmbLight))*max(cAO,$AmbLight)*((1.0-objMetallic)*objColor)+
			pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular*mix(vec3(1.0),objColor,objMetallic)*(1.0-objRoughness)*16.0;
		}
		//reflection
		d=0.01;
		march_$name(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		vec3 objEmissionRef=vec3(0);
		if (d<50.0) {
			objColorRef=mfsdf_$name(vec4(p,1.0)).xyz;
			objEmissionRef=mfsdf_$name(vec4(p,13.0)).xyz;
			n=normal_$name(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			if (objEmissionRef.x>0.0){
			  objColorRef=objEmissionRef;
			} else {
			  objColorRef=max(l,$AmbLight)*objColorRef;
			}
		} else {
			objColorRef=$hdri(Simple360HDR_equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,objSpecular*mix(vec3(1.0),objColor,objMetallic));
	} else {
		color=$hdri(Simple360HDR_equirectangularMap(rd.xzy)).xyz;
	}
	return pow(color,vec3(1.0/$Gamma));
}

shader_model.global:
vec3 MFSDF_Obj_Maker_rotate3d(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

shader_model.instance:
vec4 PBRObjectMaker_$name(vec4 uv){
    uv.xyz=MFSDF_Obj_Maker_rotate3d(uv.xyz-vec3($TranlateX,$TranlateY,$TranlateZ),vec3($RotateX,$RotateY,$RotateZ)*0.01745329251)/$scale;
	float sdf=$sdf3d(uv.xyz)*$scale;
	//13 - Emission
	if (uv.w>12.5&&uv.w<13.5) {
		return vec4($Emission*$Emission_tex3d(vec4(uv.xyz,13.0)),sdf);
	} else
	//5 - Roughness
	if (uv.w>4.5&&uv.w<5.5) {
		return vec4($Roughness*clamp($Roughness_tex3d(vec4(uv.xyz,5.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//4 - Specular
	if (uv.w>3.5) {
		return vec4($Specular*clamp($Specular_tex3d(vec4(uv.xyz,4.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//2 - Metallic
	if (uv.w>1.5&&uv.w<2.5) {
		return vec4($Metallic*clamp($Metallic_tex3d(vec4(uv.xyz,2.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//1 - BaseColor
	if (uv.w>0.5){
		return vec4($BaseColor.rgb*clamp($BaseColor_tex3d(vec4(uv.xyz,1.0)),vec3(0.0),vec3(1.0)),sdf);
	} else
	//0 - SDF
	{
		return vec4(vec3(0),sdf);
	}
}

