    vec3 color=vec3(0.0);
	vec3 objColor=$mfsdf(vec4(p,1.0)).xyz;   // 1 - BaseColor (r,g,b,sdf) linear (0-1) 
	float objSpecular=$mfsdf(vec4(p,4.0)).x*0.2;  // 4 - Specular  (v,0,0,sdf)
	float objRoughness=$mfsdf(vec4(p,5.0)).x; // 5 - Roughness (v,0,0,sdf)
	vec3 light=normalize(vec3($SunX,$SunY,$SunZ));
	if (d<50.0) {
	    vec3 n=normal_$name(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_$name(p,n),$AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_$name(p,light,0.05,5.0),$Shadow);
		color=min(vec3(max(shadow,$AmbLight)),max(l,$AmbLight))*max(cAO,$AmbLight)*objColor+4.0*pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular;
		//reflection
		d=0.01;
		march_$name(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		if (d<50.0) {
			objColorRef=$mfsdf(vec4(p,1.0)).xyz;
			n=normal_$name(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			objColorRef=max(l,$AmbLight)*objColorRef;
		} else {
			objColorRef=$hdri(r360_equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,objSpecular);
	} else {
		color=$hdri(r360_equirectangularMap(rd.xzy)).xyz;
	}
	return pow(color,vec3(1.0/$Gamma));
}

shader_model.global:
void pathtracing_Onb(in vec3 N, inout vec3 T, inout vec3 B){
    vec3 UpVector=abs(N.z)<0.999?vec3(0,0,1):vec3(1,0,0);
    T=normalize(cross(UpVector,N));
    B=cross(N,T);
}


vec2 pathtracing_equirectangularMap(vec3 dir) {

	vec2 longlat = vec2(atan(dir.y,dir.x),acos(dir.z));
 	return longlat/vec2(2.0*3.14159265359,3.14159265359);
}

// RANDOM START
//from https://www.reedbeta.com/blog/hash-functions-for-gpu-rendering/
uint pathtracing_rand_pcg(inout uint seed){
    uint state = seed;
    seed = seed * uint(747796405) + uint(2891336453);
    uint word = ((state >> ((state >> uint(28)) + uint(4))) ^ state) * uint(277803737);
    return (word >> uint(22)) ^ word;
}

float pathtracing_RandomFloat01(inout uint state){
    uint r=pathtracing_rand_pcg(state);
    r &= uint(0x007FFFFF);
    r |= uint(0x3F800000);
    return uintBitsToFloat(r) - 1.0f;
}

// RANDOM END

// SAMPLING START

vec2 pathtracing_randomPointInDisc(inout uint state, float radius){

    float u1=pathtracing_RandomFloat01(state);
    float u2=pathtracing_RandomFloat01(state);
    u2=u2*2.0*3.14159265359;
    float r = radius*sqrt(u1);
    return r*vec2(sin(u2),cos(u2));
}

vec3 pathtracing_uniformSampleSphere(float r1,float r2){
    const float TAU=6.28318530718;
    float z=1.0-2.0*r1;
    float r=sqrt(max(0.0,1.0-z*z));
    float phi=TAU*r2;
    return vec3(r*cos(phi),r*sin(phi),z);
}

vec3 pathtracing_cosineSampleHemissphere(inout uint state,vec3 n){
    float u1=pathtracing_RandomFloat01(state);
    float u2=pathtracing_RandomFloat01(state);
    return normalize(n+pathtracing_uniformSampleSphere(u1,u2)); 
}

// Adapted from "Sampling the GGX Distribution of Visible Normals",
// http://jcgt.org/published/0007/04/01/
vec3 pathtracing_SampleGGXVNDF(inout uint state,vec3 Ve,float alpha_x,float alpha_y){



    float U1=pathtracing_RandomFloat01(state);
    float U2=pathtracing_RandomFloat01(state);

    //transforming the view direction to the hemisphere configuration
    vec3 Vh = normalize(vec3(alpha_x * Ve.x, alpha_y * Ve.y, Ve.z));

    //orthonormal basis
    vec3 T1;vec3 T2;
    pathtracing_Onb(Vh,T1,T2);

    //parameterization of the projected area
    float r = sqrt(U1); 
    float phi = 2.0 * 3.14159265359 * U2;    
    float t1 = r * cos(phi);
    float t2 = r * sin(phi);
    float s = 0.5 * (1.0 + Vh.z);
    t2 = (1.0 - s)*sqrt(1.0 - t1*t1) + s*t2;

    //reprojection onto hemisphere
    vec3 Nh = t1*T1 + t2*T2 + sqrt(max(0.0f, 1.0f - t1*t1 - t2*t2))*Vh;

    //transforming the normal back to the ellipsoid configuration
    vec3 Ne = normalize(vec3(alpha_x * Nh.x, alpha_y * Nh.y, max(0.0f, Nh.z))); 

    return Ne;
}

float pathtracing_schlickWeight(float cosTheta) {
    float m=clamp(1.-cosTheta,0.,1.);
    float m2=m*m;
    return m2*m2*m;
}

// SAMPLING END

shader_model.instance:
vec4 mfsdf_$name(vec4 p){ 
  vec4 a=vec4(0.0,0.0,0.0,999999999.0);
  vec4 b;
  #for
  b=$mfsdf#(p);
  a=(a.w<b.w)?a:b;
  #end
  return a;
}

//tetrahedron normal by PauloFalcao
vec3 normal_$name(vec3 p){  
  const vec3 e=vec3(0.$Precision,-0.$Precision,0.0);
  float v1=mfsdf_$name(vec4(p+e.xyy,0.0)).w;
  float v2=mfsdf_$name(vec4(p+e.yyx,0.0)).w;
  float v3=mfsdf_$name(vec4(p+e.yxy,0.0)).w;
  float v4=mfsdf_$name(vec4(p+e.xxx,0.0)).w;
  return normalize(vec3(v4+v1-v3-v2,v3+v4-v1-v2,v2+v4-v3-v1));
}

void raymarch_$name(in vec3 o,in vec3 v,out vec3 p,inout float d){
  float s;
  for(int i=0;i<$MaxRayLoop;i++){
    p=o+v*d;
    s=mfsdf_$name(vec4(p,0.0)).w;
    if (abs(s)<0.0$Precision) break;
    d+=s;
    if (d>$MaxDistance) break;
  } 
}

vec3 pathtracing_$name(vec2 uv) {

    // Init random
    vec2 rndgen=(uv+100.0)*10000.0;
    uint rngState=uint(uint(rndgen.x)*uint(1973)+uint(rndgen.y)*uint(9277) + uint($time*100000.0)*uint(26699));

    vec3 o=vec3($CamX,$CamY,$CamZ); 
    vec3 t=vec3($LookAtX,$LookAtY,$LookAtZ);
    vec3 u=vec3(0.0,1.0,0.0);
    vec3 c=normalize(t-o);
		 o=o+c*$CamZoom;
	float planeDistance=length(t-o);
    vec3 cx=normalize(cross(u,c));
    vec3 cy=normalize(cross(cx,c));

    vec3 AAColor=vec3(0.0);

    for(int AALoop=0;AALoop<$Samples;AALoop++){

	    vec2 xy=uv.xy-0.5;

	    vec3 v=normalize(c*$CamD+cx*xy.x+cy*xy.y);

		//DOF focal plane
		float fcs=dot(v,c);
		vec3 focalPoint=o+v*(planeDistance+$DOFPlane)*1.0/fcs;
		vec2 rdisc=pathtracing_randomPointInDisc(rngState,$DOF);
		o=o+rdisc.x*cx+rdisc.y*cy;
		v=normalize(focalPoint-o);

	    vec3 p;
	    float d=0.01;
	    vec3 rgb=vec3(1.0);
	    vec3 io=o;

	    int ii=0;
	    for(int i=0;i<$MaxRefIte;i++){
	    	ii=ii+1;
	        raymarch_$name(io,v,p,d);

	        vec3 n=normal_$name(p);

	        //View direction to tangent space
	        vec3 ot;vec3 ob;pathtracing_Onb(n,ot,ob);
	        vec3 vt=vec3(dot(-v,ot),dot(-v,ob),dot(-v,n));

	        if (d<$MaxDistance){	
	        //if (length(p-o)<$MaxDistance){
	        vec3 obj_Emission=mfsdf_$name(vec4(p,13.0)).rgb;

	        if ((obj_Emission.x+obj_Emission.y+obj_Emission.z)>0.001){
	            rgb=obj_Emission*rgb;
	            break;
	        } else {
	            float s=pathtracing_RandomFloat01(rngState);

	            vec3 obj_BaseColor=mfsdf_$name(vec4(p,1.0)).rgb; // 1 - BaseColor  (r,g,b,sdf)
	            float obj_Metallic=clamp(mfsdf_$name(vec4(p,2.0)).x,0.0,1.0);   // 2 - Metallic   (v,0,0,sdf)
	            float obj_Specular=clamp(max(mfsdf_$name(vec4(p,4.0)).x,obj_Metallic),0.0,1.0);   // 4 - Specular   (v,0,0,sdf)
	            float obj_Roughness=mfsdf_$name(vec4(p,5.0)).x;  // 5 - Roughness  (v,0,0,sdf)

	            if (s>obj_Specular*obj_Specular*0.5){
	                rgb=(obj_BaseColor-obj_BaseColor*obj_Metallic)*rgb;
	                v=pathtracing_cosineSampleHemissphere(rngState,n);
	            } else {

	                float r2=obj_Roughness*obj_Roughness*0.5;
	                vec3 vndf=pathtracing_SampleGGXVNDF(rngState,vt,r2,r2);
	                vec3 nv=reflect(-vt,vndf);

	                //Sample direction back to camera space
	                nv=nv.x*ot+nv.y*ob+nv.z*n;

	                float FH=pathtracing_schlickWeight(dot(n,nv));
	                vec3 Cspec0=mix(vec3(obj_Specular*0.25),obj_BaseColor,obj_Metallic);
	                vec3 Fs=mix(Cspec0,vec3(1.0),FH);
	                rgb=Fs*rgb;
	                v=nv;
	            }
	            io=p;
	            d=0.01;
	        }
	        } else {
	        rgb*=$hdri(pathtracing_equirectangularMap(v.xzy)).xyz;

	        break;
	        }
	    }//for(int i
	    if (ii==$MaxRefIte) rgb=vec3(0.0);//did not hit any light source

	    AAColor+=max(rgb,vec3(0));

	}

	return AAColor/floor($Samples);

}

shader_model.global:
vec3 MFSDF_Obj_Maker_rotate3d(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

shader_model.instance:
vec4 PBRObjectMaker_$name(vec4 uv){
    uv.xyz=MFSDF_Obj_Maker_rotate3d(uv.xyz-vec3($TranlateX,$TranlateY,$TranlateZ),vec3($RotateX,$RotateY,$RotateZ)*0.01745329251)/$scale;
	float sdf=$sdf3d(uv.xyz)*$scale;
	//13 - Emission
	if (uv.w>12.5&&uv.w<13.5) {
		return vec4($Emission*$Emission_tex3d(vec4(uv.xyz,13.0)),sdf);
	} else
	//5 - Roughness
	if (uv.w>4.5&&uv.w<5.5) {
		return vec4($Roughness*clamp($Roughness_tex3d(vec4(uv.xyz,5.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//4 - Specular
	if (uv.w>3.5) {
		return vec4($Specular*clamp($Specular_tex3d(vec4(uv.xyz,4.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//2 - Metallic
	if (uv.w>1.5&&uv.w<2.5) {
		return vec4($Metallic*clamp($Metallic_tex3d(vec4(uv.xyz,2.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//1 - BaseColor
	if (uv.w>0.5){
		return vec4($BaseColor.rgb*clamp($BaseColor_tex3d(vec4(uv.xyz,1.0)),vec3(0.0),vec3(1.0)),sdf);
	} else
	//0 - SDF
	{
		return vec4(vec3(0),sdf);
	}
}

