shader_model.global:
void pathtracing_Onb(in vec3 N, inout vec3 T, inout vec3 B){
    vec3 UpVector=abs(N.z)<0.999?vec3(0,0,1):vec3(1,0,0);
    T=normalize(cross(UpVector,N));
    B=cross(N,T);
}


vec2 pathtracing_equirectangularMap(vec3 dir) {
    const float PI=3.14159265359;
	vec2 longlat = vec2(atan(dir.y,dir.x),acos(dir.z));
 	return longlat/vec2(2.0*PI,PI);
}

// RANDOM START
//from https://www.reedbeta.com/blog/hash-functions-for-gpu-rendering/
uint pathtracing_rand_pcg(inout uint seed){
    uint state = seed;
    seed = seed * uint(747796405) + uint(2891336453);
    uint word = ((state >> ((state >> uint(28)) + uint(4))) ^ state) * uint(277803737);
    return (word >> uint(22)) ^ word;
}

float pathtracing_RandomFloat01(inout uint state){
    uint r=pathtracing_rand_pcg(state);
    r &= uint(0x007FFFFF);
    r |= uint(0x3F800000);
    return uintBitsToFloat(r) - 1.0f;
}

// RANDOM END

// SAMPLING START

vec2 pathtracing_randomPointInDisc(inout uint state, float radius){
    const float PI=3.14159265359;
    float u1=pathtracing_RandomFloat01(state);
    float u2=pathtracing_RandomFloat01(state);
    u2=u2*2.0*PI;
    float r = radius*sqrt(u1);
    return r*vec2(sin(u2),cos(u2));
}

vec3 pathtracing_uniformSampleSphere(float r1,float r2){
    const float TAU=6.28318530718;
    float z=1.0-2.0*r1;
    float r=sqrt(max(0.0,1.0-z*z));
    float phi=TAU*r2;
    return vec3(r*cos(phi),r*sin(phi),z);
}

vec3 pathtracing_cosineSampleHemissphere(inout uint state,vec3 n){
    float u1=pathtracing_RandomFloat01(state);
    float u2=pathtracing_RandomFloat01(state);
    return normalize(n+pathtracing_uniformSampleSphere(u1,u2)); 
}

// Adapted from "Sampling the GGX Distribution of Visible Normals",
// http://jcgt.org/published/0007/04/01/
vec3 pathtracing_SampleGGXVNDF(inout uint state,vec3 Ve,float alpha_x,float alpha_y){

    const float PI=3.14159265359;

    float U1=pathtracing_RandomFloat01(state);
    float U2=pathtracing_RandomFloat01(state);

    //transforming the view direction to the hemisphere configuration
    vec3 Vh = normalize(vec3(alpha_x * Ve.x, alpha_y * Ve.y, Ve.z));

    //orthonormal basis
    vec3 T1;vec3 T2;
    pathtracing_Onb(Vh,T1,T2);

    //parameterization of the projected area
    float r = sqrt(U1); 
    float phi = 2.0 * PI * U2;    
    float t1 = r * cos(phi);
    float t2 = r * sin(phi);
    float s = 0.5 * (1.0 + Vh.z);
    t2 = (1.0 - s)*sqrt(1.0 - t1*t1) + s*t2;

    //reprojection onto hemisphere
    vec3 Nh = t1*T1 + t2*T2 + sqrt(max(0.0f, 1.0f - t1*t1 - t2*t2))*Vh;

    //transforming the normal back to the ellipsoid configuration
    vec3 Ne = normalize(vec3(alpha_x * Nh.x, alpha_y * Nh.y, max(0.0f, Nh.z))); 

    return Ne;
}

float pathtracing_schlickWeight(float cosTheta) {
    float m=clamp(1.-cosTheta,0.,1.);
    float m2=m*m;
    return m2*m2*m;
}

// SAMPLING END

