	            float obj_Specular=clamp(max($mfsdf(vec4(p,4.0)).x,obj_Metallic),0.0,1.0);   // 4 - Specular   (v,0,0,sdf)
	            float obj_Roughness=$mfsdf(vec4(p,5.0)).x;  // 5 - Roughness  (v,0,0,sdf)

	            if (s>obj_Specular*obj_Specular*0.5){
	                rgb=(obj_BaseColor-obj_BaseColor*obj_Metallic)*rgb;
	                v=pathtracing_cosineSampleHemissphere(rngState,n);
	            } else {

	                float r2=obj_Roughness*obj_Roughness*0.5;
	                vec3 vndf=pathtracing_SampleGGXVNDF(rngState,vt,r2,r2);
	                vec3 nv=reflect(-vt,vndf);

	                //Sample direction back to camera space
	                nv=nv.x*ot+nv.y*ob+nv.z*n;

	                float FH=pathtracing_schlickWeight(dot(n,nv));
