//     Gradient Noise 3D             : https://www.shadertoy.com/view/Xsl3Dl
//     Simplex  Noise 2D             : https://www.shadertoy.com/view/Msf3WH
//     Voronoise: https://www.shadertoy.com/view/Xd23Dh
//
//
//

// discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */
vec3 XsX3zB_random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

// skew constants for 3d simplex functions
const float XsX3zB_F3 =  0.3333333;
const float XsX3zB_G3 =  0.1666667;

// 3d simplex noise
float XsX3zB_simplex3d(vec3 p) {
	 // 1. find current tetrahedron T and it's four vertices
	 // s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices
	 // x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices

	 // calculate s and x
	 vec3 s = floor(p + dot(p, vec3(XsX3zB_F3)));
	 vec3 x = p - s + dot(s, vec3(XsX3zB_G3));

	 // calculate i1 and i2
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 // x1, x2, x3
	 vec3 x1 = x - i1 + XsX3zB_G3;
	 vec3 x2 = x - i2 + 2.0*XsX3zB_G3;
	 vec3 x3 = x - 1.0 + 3.0*XsX3zB_G3;

	 // 2. find four surflets and store them in d
	 vec4 w, d;

	 // calculate surflet weights
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 // w fades from 0.6 at the center of the surflet to 0.0 at the margin
	 w = max(0.6 - w, 0.0);

	 // calculate surflet components
	 d.x = dot(XsX3zB_random3(s), x);
	 d.y = dot(XsX3zB_random3(s + i1), x1);
	 d.z = dot(XsX3zB_random3(s + i2), x2);
	 d.w = dot(XsX3zB_random3(s + 1.0), x3);

	 // multiply d by w^4
	 w *= w;
	 w *= w;
	 d *= w;

	 // 3. return the sum of the four surflets
	 return dot(d, vec4(52.0));
}

float Simple_AAcheckedboard2D(vec2 p) {
    p*=2.0;
	vec2 w=max(abs(dFdx(p)),abs(dFdy(p)))+1e-5;
	if (max(w.x,w.y)>1.0) {
		return mod(floor(p.x)+floor(p.y),2.0);
	}
	vec2 i=((1.-abs(2.*fract((p+.5*w)/2.)-1.))-(1.-abs(2.*fract((p-.5*w)/2.)-1.)))/w;
	return .5-.5*i.x*i.y;
}

vec3 SDFTrans_Obj_Transform_Rot(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

