vec3 circle_repeat_transform_X(vec3 p, float count, float distance) {
	float r = 6.28/count;
	float pa = atan(p.y, p.z);
	float a = mod(pa+0.5*r, r)-0.5*r;
	vec3 rv;
	float c = cos(a-pa);
	float s = sin(a-pa);
	rv.x = p.x;
