//By pauloFalcao
vec4 distortHeighByNormal_$name(vec3 uv){
    float d=$sdf(uv);
	if (d<=abs($Distort*($Bound+1.0))+0.01){
		vec3 n=normal_$name(uv);
		vec3 s=$tex3d(vec4(uv-d*n,0.0));
	    return vec4(s,$sdf(uv-n*s*$Distort));
	} else {
		return vec4(vec3(0.0),d);
	}

}

