  s*=0.5;
  float x=asin(sin(atan(p.x,p.z)*s)/(m+1.0))/s;
  return vec3(sin(x)*length(p.xz),p.y,cos(x)*length(p.xz)-distance);
}

vec3 sdf_smoothRot_Z(vec3 p,float s,float m,float distance){
  s*=0.5;
  float x=asin(sin(atan(p.x,p.y)*s)/(m+1.0))/s;
  return vec3(sin(x)*length(p.xy),cos(x)*length(p.xy)-distance,p.z);
