vec3 smoothrepeat_$name(vec3 p,vec3 smooth_size,vec3 size){
    size.$Axis/=2.0;
    float w=pow(2.0,float($Iterations));
	for(int i=0;i<$Iterations;i++){
        p.$Axis=sqrt(p.$Axis*p.$Axis+smooth_size.$Axis);//smooth abs
		p.$Axis-=size.$Axis*w;//translate
		w=w/2.0;
	}
	return p;
}

float blakle_smoothrepeat_asin_sin_$name(vec3 p,vec3 smooth_size,vec3 size){
