float blursdf3d_$name(in vec3 p,in float b,float f){
	float a=0.0;
	float d=$in(p);
	if (d<b*f){
		for(int i=0;i<$iterations;i++){
	        float y=1.0-2.0*(float(i)/float($iterations));
	        float r=sqrt(1.0-y*y);
	        float t=2.39996322973*float(i);
			a+=$in(p+vec3(cos(t)*r,y,sin(t)*r)*b);
		}
	} else{
		return d;
	}
	return a/$iterations.0;
}

