//
// if you're looking for procedural noise implementation examples you might 
// also want to look at the following shaders:
// 
// Noise Lab shader by candycat: https://www.shadertoy.com/view/4sc3z2
//
// Noise shaders by iq:
//     Value    Noise 2D, Derivatives: https://www.shadertoy.com/view/4dXBRH
//     Gradient Noise 2D, Derivatives: https://www.shadertoy.com/view/XdXBRH
//     Value    Noise 3D, Derivatives: https://www.shadertoy.com/view/XsXfRH
//     Gradient Noise 3D, Derivatives: https://www.shadertoy.com/view/4dffRH
//     Value    Noise 2D             : https://www.shadertoy.com/view/lsf3WH
//     Value    Noise 3D             : https://www.shadertoy.com/view/4sfGzS
//     Gradient Noise 2D             : https://www.shadertoy.com/view/XdXGW8
//     Gradient Noise 3D             : https://www.shadertoy.com/view/Xsl3Dl
//     Simplex  Noise 2D             : https://www.shadertoy.com/view/Msf3WH
//     Voronoise: https://www.shadertoy.com/view/Xd23Dh
//
//
//

// using https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
// discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */
vec3 XsX3zB_oct_random3(vec3 c) {
	c = fract(c * vec3(.1031, .1030, .0973));
    c += dot(c, c.yxz+0.3333333);
    return fract((c.xxy + c.yxx)*c.zyx)-0.5;
}

// skew constants for 3d simplex functions
const float XsX3zB_oct_F3 =  0.3333333;
const float XsX3zB_oct_G3 =  0.1666667;

// 3d simplex noise
float XsX3zB_oct_simplex3d(vec3 p) {
	 // 1. find current tetrahedron T and it's four vertices
	 // s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices
	 // x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices

	 // calculate s and x
	 vec3 s = floor(p + dot(p, vec3(XsX3zB_oct_F3)));
	 vec3 x = p - s + dot(s, vec3(XsX3zB_oct_G3));

	 // calculate i1 and i2
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 // x1, x2, x3
	 vec3 x1 = x - i1 + XsX3zB_oct_G3;
	 vec3 x2 = x - i2 + 2.0*XsX3zB_oct_G3;
	 vec3 x3 = x - 1.0 + 3.0*XsX3zB_oct_G3;

	 // 2. find four surflets and store them in d
	 vec4 w, d;

	 // calculate surflet weights
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 // w fades from 0.6 at the center of the surflet to 0.0 at the margin
	 w = max(0.6 - w, 0.0);

	 // calculate surflet components
	 d.x = dot(XsX3zB_oct_random3(s), x);
	 d.y = dot(XsX3zB_oct_random3(s + i1), x1);
	 d.z = dot(XsX3zB_oct_random3(s + i2), x2);
	 d.w = dot(XsX3zB_oct_random3(s + 1.0), x3);

	 // multiply d by w^4
	 w *= w;
	 w *= w;
	 d *= w;

	 // 3. return the sum of the four surflets
	 return dot(d, vec4(52.0));
}

shader_model.instance:
float $(name)_fbm(vec3 coord, float persistence) {
	float normalize_factor = 0.0;
	float value = 0.0;
	float scale = 1.0;
	float size = 1.0;
	for (int i = 0; i < $iterations; i++) {
		value += XsX3zB_oct_simplex3d(coord*size) * scale;
		normalize_factor += scale;
		size *= 2.0;
		scale *= persistence;
	}
	return value / normalize_factor;
}

float $(name)_bc(float f,float contrast, float brightness) {
	return f*contrast+brightness+0.5-contrast*0.5;
}

shader_model.global:
vec3 SDFTrans_Obj_Transform_Rot(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

float Simple_AAcheckedboard2D(vec2 p) {
    p*=2.0;
	vec2 w=max(abs(dFdx(p)),abs(dFdy(p)))+1e-5;
	if (max(w.x,w.y)>1.0) {
		return mod(floor(p.x)+floor(p.y),2.0);
	}
	vec2 i=((1.-abs(2.*fract((p+.5*w)/2.)-1.))-(1.-abs(2.*fract((p-.5*w)/2.)-1.)))/w;
	return .5-.5*i.x*i.y;
}

