float for_min_$name(vec4 p) {
  float d=0.0;
  float m=999999.0;
  if ($minite==0){
	  d=$obj3d(p.xyz);
	  m=min(999999.0,d);
  }
  for(int i=0;i<$iterations;i++){
    p=$trans3d(p);
	d=$obj3d(p.xyz);
	if (i>=$minite) m=min(m,d/p.w);
  }
  return m;
