//tetrahedron normal by PauloFalcao
//https://www.shadertoy.com/view/XstGDS
vec3 normal_$name(vec3 p){  
  const vec3 e=vec3(0.001,-0.001,0.0);
  float v1=input_$name(p+e.xyy).x;
  float v2=input_$name(p+e.yyx).x;
  float v3=input_$name(p+e.yxy).x;
  float v4=input_$name(p+e.xxx).x;
  return normalize(vec3(v4+v1-v3-v2,v3+v4-v1-v2,v2+v4-v3-v1));
}

void march_$name(inout float d,inout vec3 p,inout vec2 dS, vec3 ro, vec3 rd){
    for (int i=0; i < 500; i++) {
    	p = ro + rd*d;
        dS = input_$name(p);
        d += dS.x;
        if (d > 50.0 || abs(dS.x) < 0.0001) break;
    }
}

//from https://www.shadertoy.com/view/lsKcDD
float calcAO_$name( in vec3 pos, in vec3 nor ){
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ){
        float h = 0.001 + 0.25*float(i)/4.0;
        float d = input_$name( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.98;
    }
    return clamp( 1.0 - 1.6*occ, 0.0, 1.0 );    
}

//from https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow_$name( in vec3 ro, in vec3 rd, in float mint, in float tmax){
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration
    for( int i=0; i<32; i++ ){
		float h = input_$name( ro + rd*t ).x;
        res = min( res, 10.0*h/t );
        t += h;
        if( res<0.0001 || t>tmax ) break;  
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 raymarch_$name(vec2 uv) {
    uv-=0.5;
	vec3 cam=vec3($CamX,$CamY,$CamZ)*$CamZoom;
	vec3 lookat=vec3($LookAtX,$LookAtY,$LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*$CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	vec2 dS=vec2(0);
	march_$name(d,p,dS,ro,rd);

    vec3 color=vec3(0.0);

	vec3 objColor=tex_$name(p,dS.y);

	vec3 light=normalize(vec3($SunX,$SunY,$SunZ));
	if (d<50.0) {
	    vec3 n=normal_$name(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_$name(p,n),$AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_$name(p,light,0.05,5.0),$Shadow);
		color=min(vec3(max(shadow,$AmbLight)),max(l,$AmbLight))*max(cAO,$AmbLight)*objColor+pow(r,$Pow)*$Specular;
		//reflection
		d=0.01;
		march_$name(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		if (d<50.0) {
			objColorRef=tex_$name(p,dS.y);
			n=normal_$name(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			objColorRef=max(l,$AmbLight)*objColorRef;
		} else {
			objColorRef=$hdri(equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,$Reflection);
	} else {
		color=$hdri(equirectangularMap(rd.xzy)).xyz;
	}
	return color;
}

