vec3 sdf2d_diff_$name(vec2 xy) {
	xy-=0.5;
	vec3 col=vec3(0.0);
	float t=0.0;

	#for
		if (sdfselector_$name(vec3(0.5,xy.y,xy.x))<0.0) col+=vec3(1.0);
