vec4 PBRObjectMaker_$name(vec4 uv){
    uv.xyz=MFSDF_Obj_Maker_rotate3d(uv.xyz-vec3($TranlateX,$TranlateY,$TranlateZ),vec3($RotateX,$RotateY,$RotateZ)*0.01745329251)/$scale;
	float sdf=$sdf3d(uv.xyz)*$scale;
	//13 - Emission
	if (uv.w>12.5&&uv.w<13.5) {
		return vec4($Emission*$Emission_tex3d(vec4(uv.xyz,13.0)),sdf);
	} else
	//5 - Roughness
	if (uv.w>4.5&&uv.w<5.5) {
		return vec4($Roughness*clamp($Roughness_tex3d(vec4(uv.xyz,5.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//4 - Specular
	if (uv.w>3.5) {
		return vec4($Specular*clamp($Specular_tex3d(vec4(uv.xyz,4.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//2 - Metallic
	if (uv.w>1.5&&uv.w<2.5) {
		return vec4($Metallic*clamp($Metallic_tex3d(vec4(uv.xyz,2.0)),0.0,1.0),0.0,0.0,sdf);
	} else
	//1 - BaseColor
	if (uv.w>0.5){
		return vec4($BaseColor.rgb*clamp($BaseColor_tex3d(vec4(uv.xyz,1.0)),vec3(0.0),vec3(1.0)),sdf);
	} else
	//0 - SDF
	{
		return vec4(vec3(0),sdf);
	}
}

