    float sca = 1.0;
    for( int i=0; i<5; i++ ){
        float h = 0.001 + 0.25*float(i)/4.0;
        float d = mfsdf_$name(vec4( pos + h*nor ,0.0)).w;
        occ += (h-d)*sca;
        sca *= 0.98;
    }
    return clamp( 1.0 - 1.6*occ, 0.0, 1.0 );    
}

//from https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow_$name( in vec3 ro, in vec3 rd, in float mint, in float tmax){
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration
    for( int i=0; i<32; i++ ){
	    vec3 p=ro + rd*t;
		float h = mfsdf_$name(vec4(p,0.0)).w;
		float isLight=mfsdf_$name(vec4(p,13.0)).x;
		if (isLight>0.0) h=999999.0;
        res = min( res, 10.0*h/t );
        t += h;
        if( res<0.0001 || t>tmax ) break;  
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 raymarch_$name(vec2 uv) {
    uv-=0.5;
	vec3 cam=vec3($CamX,$CamY,$CamZ)*$CamZoom;
	vec3 lookat=vec3($LookAtX,$LookAtY,$LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*$CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	float dS=0.0;
	march_$name(d,p,dS,ro,rd);

    vec3 color=vec3(0.0);
	vec3 objColor=mfsdf_$name(vec4(p,1.0)).xyz;   // 1 - BaseColor (r,g,b,sdf) linear (0-1)
	float objMetallic=clamp(mfsdf_$name(vec4(p,2.0)).x,0.0,1.0);  // 2 - Metallic  (v,0,0,sdf)
	float objSpecular=clamp(max(mfsdf_$name(vec4(p,4.0)).x*0.2,objMetallic),0.0,1.0);  // 4 - Specular  (v,0,0,sdf)
	float objRoughness=mfsdf_$name(vec4(p,5.0)).x; // 5 - Roughness (v,0,0,sdf)
	vec3 objEmission=mfsdf_$name(vec4(p,13.0)).xyz; // 13 - Emission (r,g,b,sdf)
	objColor=max(objColor,objEmission);
	vec3 light=normalize(vec3($SunX,$SunY,$SunZ));
	if (d<50.0) {
	    vec3 n=normal_$name(p);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_$name(p,n),$AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_$name(p,light,0.05,5.0),$Shadow);
		if (objEmission.x>0.0){
			color=objEmission;
		} else {
			color=min(vec3(max(shadow,$AmbLight)),max(l,$AmbLight))*max(cAO,$AmbLight)*((1.0-objMetallic)*objColor)+
			pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular*mix(vec3(1.0),objColor,objMetallic)*(1.0-objRoughness)*16.0;
		}
		//reflection
		d=0.01;
		march_$name(d,p,dS,p,ref);
		vec3 objColorRef=vec3(0);
		vec3 objEmissionRef=vec3(0);
		if (d<50.0) {
			objColorRef=mfsdf_$name(vec4(p,1.0)).xyz;
			objEmissionRef=mfsdf_$name(vec4(p,13.0)).xyz;
			n=normal_$name(p);
			l=clamp(dot(-light,-n),0.0,1.0);
			if (objEmissionRef.x>0.0){
			  objColorRef=objEmissionRef;
			} else {
			  objColorRef=max(l,$AmbLight)*objColorRef;
			}
		} else {
			objColorRef=$hdri(Simple360HDR_equirectangularMap(ref.xzy)).xyz;
		}
		color=mix(color,objColorRef,objSpecular*mix(vec3(1.0),objColor,objMetallic));
	} else {
		color=$hdri(Simple360HDR_equirectangularMap(rd.xzy)).xyz;
	}
	return pow(color,vec3(1.0/$Gamma));
}

shader_model.global:
vec3 rotate3d_with_modulation(vec3 p, vec3 a) {
	vec3 rv;
	float c;
	float s;
	c = cos(a.x);
	s = sin(a.x);
	rv.x = p.x;
	rv.y = p.y*c+p.z*s;
	rv.z = -p.y*s+p.z*c;
	c = cos(a.y);
	s = sin(a.y);
	p.x = rv.x*c+rv.z*s;
	p.y = rv.y;
	p.z = -rv.x*s+rv.z*c;
	c = cos(a.z);
	s = sin(a.z);
	rv.x = p.x*c+p.y*s;
	rv.y = -p.x*s+p.y*c;
	rv.z = p.z;
	return rv;
}

vec2 twist_rot(vec2 v, float a) {
	a=a*0.01745329251;
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(vec2(c, -s),vec2(s, c));
	return m * v;
}

vec3 twistX(vec3 v, float a){
	v.yz=twist_rot(v.yz,v.x*a);
	return v;
}

vec3 twistY(vec3 v, float a){
	v.xz=twist_rot(v.xz,v.y*a);
	return v;
}

vec3 twistZ(vec3 v, float a){
	v.xy=twist_rot(v.xy,v.z*a);
	return v;
}

//based on fOpUnionChamfer from https://mercury.sexy/hg_sdf/

float fOpUnionChamfer(float a, float b, float r) {
	return min(min(a, b), (a - r + b)*sqrt(0.5));
}

//based on fOpUnionStairs from https://mercury.sexy/hg_sdf/

float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2.0 * s)) - s)));
}

vec2 equirectangularMap(vec3 dir) {
	vec2 longlat = vec2(atan(dir.y,dir.x),acos(dir.z));
 	return longlat/vec2(2.0*3.14159265359,3.14159265359);
}


//Simple HDRI START

//Hash without Sine Dave_Hoskins
//https://www.shadertoy.com/view/4djSRW 
float Simple360HDR_hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float Simple360HDR_noise(vec2 v){
  vec2 v1=floor(v);
  vec2 v2=smoothstep(0.0,1.0,fract(v));
  float n00=Simple360HDR_hash12(v1);
  float n01=Simple360HDR_hash12(v1+vec2(0,1));
  float n10=Simple360HDR_hash12(v1+vec2(1,0));
  float n11=Simple360HDR_hash12(v1+vec2(1,1));
  return mix(mix(n00,n01,v2.y),mix(n10,n11,v2.y),v2.x);
}

float Simple360HDR_noiseOct(vec2 p){
  return
    Simple360HDR_noise(p)*0.5+
    Simple360HDR_noise(p*2.0+13.0)*0.25+
    Simple360HDR_noise(p*4.0+23.0)*0.15+
    Simple360HDR_noise(p*8.0+33.0)*0.10+
    Simple360HDR_noise(p*16.0+43.0)*0.05;
}

vec3 Simple360HDR_skyColor(vec3 p){
	vec3 s1=vec3(0.2,0.5,1.0);
	vec3 s2=vec3(0.1,0.2,0.4)*1.5;
    vec3 v=(Simple360HDR_noiseOct(p.xz*0.1)-0.5)*vec3(1.0);
	float d=length(p);
    return mix(s2+v,s1+v*(12.0/max(d,20.0)),clamp(d*0.1,0.0,1.0));
}

vec3 Simple360HDR_floorColor(vec3 p){
    vec3 v=(Simple360HDR_noiseOct(p.xz*0.1)*0.5+0.25)*vec3(0.7,0.5,0.4);
    return v;
}

vec3 Simple360HDR_renderHDR360(vec3 rd, vec3 sun){
    vec3 col;
	vec3 p;
	vec3 c;
	if (rd.y>0.0) {
        p=rd*(5.0/rd.y);
        c=Simple360HDR_skyColor(p);
    } else {
        p=rd*(-10.0/rd.y);
        c=Simple360HDR_floorColor(p);
		c=mix(c,vec3(0.5,0.7,1.0),clamp(1.0-sqrt(-rd.y)*3.0,0.0,1.0));
	}
	vec3 skycolor=vec3(0.1,0.45,0.68);
	float d=length(p);

	float ds=clamp(dot(sun,rd),0.0,1.0);
	vec3 sunc=(ds>0.9997?vec3(2.0):vec3(0.0))+pow(ds,512.0)*4.0+pow(ds,128.0)*vec3(0.5)+pow(ds,4.0)*vec3(0.5);
    if (rd.y>0.0){
		c+=vec3(0.3)*pow(1.0-abs(rd.y),3.0)*0.7;
	} 
    return c+sunc;
}

vec3 Simple360HDR_make360hdri(vec2 p, vec3 sun){
    float xPI=3.14159265359;
    vec2 thetaphi = ((p * 2.0) - vec2(1.0)) * vec2(xPI,xPI/2.0); 
    vec3 rayDirection = vec3(cos(thetaphi.y) * cos(thetaphi.x), sin(thetaphi.y), cos(thetaphi.y) * sin(thetaphi.x));
    return Simple360HDR_renderHDR360(rayDirection,sun);
}
//Simple HDRI END

