// Day at the Lake by nimitz, 2020 (twitter: @stormoid)
// https://www.shadertoy.com/view/wl3czN
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
// Contact the author for other licensing options
//
// Simple floor by PauloFalcao
//
// Modified for Material Maker by (twitter: @PauloFalcao)
//

mat3 nz_cloudrot_y(float a){
	float sa = sin(a);
	float ca = cos(a);
	return mat3(vec3(ca,.0,sa),vec3(.0,1.,.0),vec3(-sa,.0,ca));
}

vec3 nz_cloudsIntcPlane(vec3 ro, vec3 rd, float plH){
    ro.y += plH;
    float t = -ro.y/rd.y;
    if (t < 0.)
        return vec3(1e6);
    float u =  ro.x + rd.x*t;
    float v =  ro.z + rd.z*t;
    return vec3(t,u,v);
}

vec4 nz_cloudMap(vec3 p, float time){
    p.xz += vec2(-time*1.0, time*0.25);
    time *= 0.25;
    p.y -= 9.0;
    p *= vec3(0.19,0.3,0.19)*0.45;
    vec3 bp = p;
    float rz = 0.;
    vec3 drv = vec3(0);

    float z = 0.5;
    float trk= 0.9;
    float dspAmp = 0.2;

    float att = clamp(1.31-abs(p.y - 5.5)*0.095,0.,1.);
    float off = dot(sin(p*.52)*0.7+0.3, cos(p.yzx*0.6)*0.7+0.3)*0.75 - 0.2; //large structures
    float ofst = 12.1 - time*0.1;

    for (int i = 0; i<6; i++){
        p += sin(p.yzx*trk - trk*2.0)*dspAmp;

        vec3 c = cos(p);
        vec3 s = sin(p);
        vec3 cs = cos(p.yzx + s.xyz + ofst);
        vec3 ss = sin(p.yzx + s.xyz + ofst);
        vec3 s2 = sin(p + s.zxy + ofst);
        vec3 cdrv = (c*(cs - s*ss) - s*ss.yzx - s.zxy*s2)*z;

        rz += (dot(s, cs) + off - 0.1)*z; //cloud density
        rz *= att;
        drv += cdrv;

        p += cdrv*0.09;
        p.xz += time*0.1;

        dspAmp *= 0.7;
        z *= 0.57;
        trk *= 2.1;
        p *= mat3(
		  vec3(0.33338, 0.56034, -0.71817),
		  vec3(-0.87887, 0.32651, -0.15323),
		  vec3(0.15162, 0.69596, 0.61339)
		)*2.01;
    }

    return vec4(rz, drv);
}

//-----------------------------Sky rendering---------------------------
// modified from an prototype public domain implementation by shadertoy user Jodie: https://www.shadertoy.com/view/ttSGzh
// That version has some serious accuracy issues, I modified it to somewhat match the Hosek-Wilkie sky model visuals.


vec3 nz_getThickness(vec3 rd){

	const float earthRadius=6371000.;
	const float atmoDepth=8228.;
	const float mieDepth=1800.0;
	const float ozoneHeight=30000.;

    const vec4 cns = earthRadius + 
        			vec4(atmoDepth, mieDepth, ozoneHeight, ozoneHeight + atmoDepth);
    const float r2 = earthRadius * earthRadius;
    float b = -rd.y*earthRadius;
    vec4 z = sqrt( cns*cns + (b*b - r2) );
    return vec3(b + z.xy, z.w - z.z);
}


vec3 nz_getSky(vec3 rd, vec3 lgt, float addDepth){

	const vec3 rayleighCoefficient=vec3(5.6e-6  , 1.25e-5 , 2.9e-5 );
	const float mieCoefficient=16e-6;// adjust for foggier look
	const vec3 ozoneCoefficient=(vec3(3.426, 8.298,.356) * 6e-5 / 100.);
	const vec3 sunColor=vec3(.95,.96,1.2 );

    const vec3 addCol = vec3(1.,1.,1.);
    const mat3 coeffs = mat3(rayleighCoefficient, vec3(mieCoefficient), ozoneCoefficient)/0.693147;
    vec3 thickness = nz_getThickness(rd) + addDepth*addCol;
    float rdl = max(dot(rd, lgt), 0.0);

    vec3 rayleighScatter =(thickness.x * (0.4*rdl*rdl + 1.12))* rayleighCoefficient;
    float g = 0.8 - lgt.y*0.15 - mieCoefficient*1500.;
    float g2 = g*g;
    float a = inversesqrt(g2 - (2.0*g*rdl - 1.0));
    float phaseMie = (0.4 + lgt.y*0.1 + mieCoefficient*7000.)*(1.0 - g2)*a*a*a;
    float mieScatter = thickness.y * phaseMie * mieCoefficient;

    vec3 sunCoeff = coeffs*(nz_getThickness(lgt) + addDepth*addCol);
    vec3 viewCoeff = coeffs*thickness;
    vec3 absorption = (exp2(-viewCoeff) - exp2(-sunCoeff))/((sunCoeff - viewCoeff)*0.693147);

    return vec3(clamp(sunColor*(rayleighScatter + mieScatter)*absorption*(0.6 + lgt.y*0.3), 0., 100.5));
}



//-------------------------End of sky rendering------------------------
//---------------------------------------------------------------------

vec3 nz_getSun(vec3 rd, float sunCoeff, vec3 lgt){
    float rdl = max(dot(rd, lgt), 0.0);
    float dcy = smoothstep(-0.05,0.2,lgt.y);
    vec3 sun = 50.0*vec3(1.,1.,.4)*pow(rdl, 10000.)*clamp(1.0 - dcy*.7, 0.,1.);
    sun += 60.0*vec3(1.,0.7,.5)*pow(rdl, 3500.);
    sun *= clamp(1.0 - dcy*.6, 0.,1.);
    sun *= exp2(-sunCoeff*0.45);
    return sun;
}


vec4 nz_getClouds( in vec3 ro, in vec3 rd, float scnD, vec3 skyCol, float time, vec3 lgt){
	vec4 rez = vec4(0.);
	float t = min(nz_cloudsIntcPlane(ro, rd, -12.).x, 175.);

    float updl = dot(lgt, vec3(0, 1, 0));
    float sunUp = max(updl, 0.);
    float sunUp3 = clamp(sunUp*sunUp*sunUp*6., 0.0, 1.0);
    float sunDw = 1.0 - sunUp;
    sunDw = pow(sunDw, 6.0);

    float bsd = dot(lgt.xz, rd.xz);
    float sd = max(bsd, 0.0);
    float sd2 = max(bsd*0.55 + 0.53,0.);

    vec3 topSky = clamp(nz_getSky(vec3(0, 1, 0), lgt, 0.).xyz, 0.,1.);
    vec3 baseCol = mix(topSky, vec3(0.05, 0.066, 0.09), clamp(sunUp3,0.0, 0.6))*0.2;

    float extraDepth = 14e4*((1.0-bsd)*0.2 + 0.9)*pow(1.0 - sunUp, 4.0);
    vec3 hlitCol = clamp(nz_getSky(normalize((lgt-vec3(0,0.001,0))*nz_cloudrot_y(0.15)), lgt, extraDepth).xyz ,0.0, 1.0);
    hlitCol = hlitCol*sd2*mix(sunDw, sunDw*sunDw, sd)*16.;

    vec3 bSkyCol = nz_getSky(rd, lgt, extraDepth*5.0*(sunUp*0.8 + 0.2)).xyz;
    vec3 sunLCol = mix(vec3(1.0, 0.8, 0.75), vec3(.5), sunUp);
    vec3 atmoCol = mix(bSkyCol*(sunUp*0.3+0.7), sunLCol*dot(bSkyCol, vec3(0.38)), sunUp3)*0.25;

	for(int i=0; i<85; i++){
		vec3 pos = ro + t*rd;
        if(rez.a > 0.99 || t > 300.  || pos.y > 150.)break;
        vec4 cld = nz_cloudMap(pos, time);
		float den = clamp(cld.x, 0., 1.)*1.02;
		float dn = clamp((cld.x + 1.9),0.0 , 3.0);
        float fogT = 0.;

		vec4 col = vec4(0);

        if (cld.x > 0.6){   
            col = vec4(baseCol, 0.1);
            float nl = max(dot(cld.yzw, -lgt), -1.2);
            float dif = clamp((cld.x - nz_cloudMap(pos + lgt*8., time).x)*0.4 + 0.2, 0.11, 2.0 )*2.5;
            dif += clamp((cld.x - nz_cloudMap(pos + lgt*15., time).x)*0.2 - 0.03, -0.02, 1. )*4.0;

            col.rgb += atmoCol*((nl*0.25 + 0.7)*dif*0.65); //atmosphere lighting

            float den2 = den*den;
            float den4 = den2*den2;
            col *= den4*den4;
            col.rgb += col.a*clamp((nl*0.8 + 0.1)*dif,0.,1.)*hlitCol; //twi-lights
            //col *= smoothstep(t-0.0, t+.1, scnD); //blend with scene
        }

        float fogC = exp2(t*0.012/(rd.y + 0.35) - 11.7);
        col.rgba += vec4(skyCol, 1.0)*clamp(fogC, 0.0, 1.0);
		rez = rez + col*(1.0 - rez.a);
        t += clamp(7. - dn*dn*.85, 1.2, 7.);
	}    
	return clamp(rez, 0.0, 1.0);
}



//Hash without Sine Dave_Hoskins
//https://www.shadertoy.com/view/4djSRW 
float npxf_hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float pxf_nz_noise(vec2 v){
  vec2 v1=floor(v);
  vec2 v2=smoothstep(0.0,1.0,fract(v));
  float n00=npxf_hash12(v1);
  float n01=npxf_hash12(v1+vec2(0,1));
  float n10=npxf_hash12(v1+vec2(1,0));
  float n11=npxf_hash12(v1+vec2(1,1));
  return mix(mix(n00,n01,v2.y),mix(n10,n11,v2.y),v2.x);
}

float pxf_nz_noiseOct(vec2 p){
  return
    pxf_nz_noise(p)*0.5+
    pxf_nz_noise(p*2.0+13.0)*0.25+
    pxf_nz_noise(p*4.0+23.0)*0.15+
    pxf_nz_noise(p*8.0+33.0)*0.10+
    pxf_nz_noise(p*16.0+43.0)*0.05;
}

vec3 pxf_nz_floorColor(vec3 p){
    vec3 v=(pxf_nz_noiseOct(p.xz*0.1)*0.5+0.25)*vec3(0.7,0.5,0.4);
    return v;
}



vec3 pxf_nz_renderHDR360( in vec3 rd, float time, vec3 lgt){

    vec3 col;
    if (rd.y>-0.35) {
        //Sky
        //Sky
        float ldt = clamp(dot(lgt,rd),0.,1.);

        vec3 skyCol = nz_getSky(rd, lgt, 0.);
        col = skyCol.rgb;
        vec3 bg= col;

        float powerSun=1.0+pow(ldt,2.0);

        col += (nz_getSun(rd,0.0,lgt)+0.05)*powerSun;

        float powerCloud=1.0+pow(ldt,32.0)*3.5;

        vec4 cld = nz_getClouds(vec3(0,0,0), rd, 10000., bg, time, lgt)*powerCloud;
        col = col*(clamp(1.0-cld.w,0.0,1.0)) + cld.xyz;
        //Floor
        vec3 p=rd*(-10.0/rd.y);
        if ((rd.y>-0.35)&&(rd.y<-0.0)){
          col=mix(col,pxf_nz_floorColor(p),clamp(-rd.y*3.0,0.0,1.0));
        }
    } else{
        vec3 p=rd*(-10.0/rd.y);
        col=pxf_nz_floorColor(p);
    }
    return col;
}


vec3 pxf_nz_make360hdri(vec2 p,float t,vec3 sunPos){
    vec2 thetaphi = ((p * 2.0) - vec2(1.0)) * vec2(3.14159265359,3.14159265359/2.0); 
    vec3 rayDirection = vec3(cos(thetaphi.y) * cos(thetaphi.x), sin(thetaphi.y), cos(thetaphi.y) * sin(thetaphi.x));

    return max(pxf_nz_renderHDR360(rayDirection,t,sunPos),vec3(0.0));
}

vec2 smoothRot_XY(vec2 p,float s,float m,float c,float distance){
  s*=0.5;
  float d=length(p);
  float x=asin(sin(atan(p.x,p.y)*s)*(1.0-m))*d;
  float ds=d*s;
  float y=mix(ds,2.0*ds-sqrt(x*x+ds*ds),c);
  return vec2(x/s,y/s-distance);
}

vec3 blendf_normal(vec3 c1, vec3 c2, float opacity) {
	return opacity*c1 + (1.0-opacity)*c2;
}

vec3 blendf_multiply(vec3 c1, vec3 c2, float opacity) {
	return opacity*c1*c2 + (1.0-opacity)*c2;
}

vec3 blendf_screen( vec3 c1, vec3 c2, float opacity) {
	return opacity*(1.0-(1.0-c1)*(1.0-c2)) + (1.0-opacity)*c2;
}

float blendf_overlay_f(float c1, float c2) {
	return (c1 < 0.5) ? (2.0*c1*c2) : (1.0-2.0*(1.0-c1)*(1.0-c2));
}

vec3 blendf_overlay(vec3 c1, vec3 c2, float opacity) {
	return opacity*vec3(blendf_overlay_f(c1.x, c2.x), blendf_overlay_f(c1.y, c2.y), blendf_overlay_f(c1.z, c2.z)) + (1.0-opacity)*c2;
}

vec3 blendf_hard_light(vec3 c1, vec3 c2, float opacity) {
	return opacity*0.5*(c1*c2+blendf_overlay(c1, c2, 1.0)) + (1.0-opacity)*c2;
}

float blendf_soft_light_f(float c1, float c2) {
	return (c2 < 0.5) ? (2.0*c1*c2+c1*c1*(1.0-2.0*c2)) : 2.0*c1*(1.0-c2)+sqrt(c1)*(2.0*c2-1.0);
}

vec3 blendf_soft_light( vec3 c1, vec3 c2, float opacity) {
	return opacity*vec3(blendf_soft_light_f(c1.x, c2.x), blendf_soft_light_f(c1.y, c2.y), blendf_soft_light_f(c1.z, c2.z)) + (1.0-opacity)*c2;
}

float blendf_burn_f(float c1, float c2) {
	return (c1==0.0)?c1:max((1.0-((1.0-c2)/c1)),0.0);
}

vec3 blendf_burn(vec3 c1, vec3 c2, float opacity) {
	return opacity*vec3(blendf_burn_f(c1.x, c2.x), blendf_burn_f(c1.y, c2.y), blendf_burn_f(c1.z, c2.z)) + (1.0-opacity)*c2;
}

float blendf_dodge_f(float c1, float c2) {
	return (c1==1.0)?c1:min(c2/(1.0-c1),1.0);
}

vec3 blendf_dodge(vec3 c1, vec3 c2, float opacity) {
	return opacity*vec3(blendf_dodge_f(c1.x, c2.x), blendf_dodge_f(c1.y, c2.y), blendf_dodge_f(c1.z, c2.z)) + (1.0-opacity)*c2;
}

vec3 blendf_lighten(vec3 c1, vec3 c2, float opacity) {
	return opacity*max(c1, c2) + (1.0-opacity)*c2;
}

vec3 blendf_darken(vec3 c1, vec3 c2, float opacity) {
	return opacity*min(c1, c2) + (1.0-opacity)*c2;
}

vec3 blendf_difference(vec3 c1, vec3 c2, float opacity) {
	return opacity*clamp(c2-c1, vec3(0.0), vec3(1.0)) + (1.0-opacity)*c2;
}

shader_model.instance:
vec4 for_blend_$name(vec2 p) {
  vec2 t=p;
  vec4 o=$img(t);
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
	vec4 no=$img(t);
	o=vec4(blendf_$blend_type(o.xyz,no.xyz,$opacity),mix(o.w,no.w,$opacity));
  }
  return o;
}

vec4 for_avg_$name(vec2 p) {
  vec2 t=p;
  vec4 no=$img(t);
  vec4 o=vec4(no.xyz*$opacity,no.w);
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
	no=$img(t);
	o+=vec4(no.xyz*$opacity,no.w);
  }
  return vec4(o/($iterations.0+1.0));
}

vec4 for_fold_$name(vec2 p) {
  vec2 t=p;
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
  }
  vec4 no=$img(t);
  vec4 o=vec4(no.xyz*$opacity,no.w);
  return o;
}

