vec3 blendf_lighten(vec3 c1, vec3 c2, float opacity) {
	return opacity*max(c1, c2) + (1.0-opacity)*c2;
}

vec3 blendf_darken(vec3 c1, vec3 c2, float opacity) {
	return opacity*min(c1, c2) + (1.0-opacity)*c2;
}

vec3 blendf_difference(vec3 c1, vec3 c2, float opacity) {
	return opacity*clamp(c2-c1, vec3(0.0), vec3(1.0)) + (1.0-opacity)*c2;
}

shader_model.instance:
vec4 for_blend_$name(vec2 p) {
  vec2 t=p;
  vec4 o=$img(t);
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
	vec4 no=$img(t);
	o=vec4(blendf_$blend_type(o.xyz,no.xyz,$opacity),mix(o.w,no.w,$opacity));
  }
  return o;
}

vec4 for_avg_$name(vec2 p) {
  vec2 t=p;
  vec4 no=$img(t);
  vec4 o=vec4(no.xyz*$opacity,no.w);
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
	no=$img(t);
	o+=vec4(no.xyz*$opacity,no.w);
  }
  return vec4(o/($iterations.0+1.0));
}

vec4 for_fold_$name(vec2 p) {
  vec2 t=p;
  for(int i=0;i<$iterations;i++){
    t=$trans2d(t).xy;
  }
  vec4 no=$img(t);
  vec4 o=vec4(no.xyz*$opacity,no.w);
  return o;
}

shader_model.global:
vec2 scale(vec2 uv, vec2 center, vec2 scale) {
	uv -= center;
	uv /= scale;
	uv += center;
    return uv;
}

// RANDOM START
//from https://www.reedbeta.com/blog/hash-functions-for-gpu-rendering/
uint magicaa_rand_pcg(inout uint seed){
    uint state = seed;
    seed = seed * uint(747796405) + uint(2891336453);
    uint word = ((state >> ((state >> uint(28)) + uint(4))) ^ state) * uint(277803737);
    return (word >> uint(22)) ^ word;
}

float magicaa_RandomFloat01(inout uint state){
    uint r=magicaa_rand_pcg(state);
    r &= uint(0x007FFFFF);
    r |= uint(0x3F800000);
    return uintBitsToFloat(r) - 1.0f;
}

// RANDOM END

shader_model.instance:
vec3 randomAA_$name(vec2 uv){
	float dx = dFdx(uv.x);
	float dy = dFdy(uv.y);

	// Init random
	vec2 rndgen=vec2(
				fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453),
				fract(sin(dot(uv, vec2(37.1235, 67.734))) * 73748.3423))*17243.0;
    uint rngState=uint(uint(rndgen.x)*uint(1973)+uint(rndgen.y)*uint(9277) + uint($time*1000.0) * uint(26699)) | uint(1);

	vec2 xy;

	vec3 aa=vec3(0.0);
	for(int i=0;i<$Samples;i++){
		xy=((uv.xy+(vec2(magicaa_RandomFloat01(rngState),magicaa_RandomFloat01(rngState))-0.5)*vec2(dx,dy)));
		aa+=$in(xy);
	}
	return pow(aa/float($Samples),1.0/vec3($Gamma));
}

shader_model.global:
float random2d1d(vec2 c,float seed) {
	c = fract(c*17.15)+5.0+seed;
	c += dot(c.x, c.y);
	return fract(c.x*c.x*c.y);
}

vec4 coord_union(vec4 a, vec4 b) {
    if(length(a.xyz)<length(b.xyz)){
		return vec4(a);
	} else {
		return vec4(b);
	}
}
